import CurrentUserContext from "../contexts/CurrentUserContext";
import { useContext } from "react";

function Card({card, onCardClick, onCardLike, onCardDelete}) {
  const currentUser = useContext(CurrentUserContext)

  function handleClick() {
    onCardClick(card);
  }

  function handleLikeClick() {
    onCardLike(card);
  }

  function handleDeleteClick() {
    onCardDelete(card);
  }

  // Определяем, являемся ли мы владельцем текущей карточки
  const isOwn = card.owner._id === currentUser._id;
  console.log(isOwn)

// Далее в разметке используем переменную для условного рендеринга
  const deleteButtonClassName = `${isOwn ? '' : 'element__delete-card'}`;

// Определяем, есть ли у карточки лайк, поставленный текущим пользователем
  const isLiked = card.likes.some(i => i._id === currentUser._id);

// Создаём переменную, которую после зададим в `className` для кнопки лайка
  const cardLikeButtonClassName = `element__like-photo ${isLiked ? '.element__like-photo_active' : ''}`;

  return (
    <div className="element">
      <button
        className={deleteButtonClassName}
        id="element__delete-card"
        type="button"
        aria-label="Удалить"
        onClick={handleDeleteClick}
      ></button>
      <img
        className="element__img"
        src={card.link}
        alt={card.name}
        onClick={handleClick}
      />
      <div className="element__info">
        <h2 className="element__title">{card.name}</h2>
        <div className="element__like-container">
          <button
            className={cardLikeButtonClassName}
            type="button"
            aria-label="Нравится"
            onClick={handleLikeClick}
          ></button>
          <p className="element__like-number">{card.likes.length}</p>
        </div>
      </div>
    </div>
  );
}

export default Card;
